package basicJava;

public class IfCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//check number is even or odd
		int a = 35;
		if (a%2 ==0) {
			System.out.println(a + "is even number");
		}else{
			System.out.println(a + "is odd number");
		}

	}

}
